import React, { useState } from "react";
import locations from "../locations.json";

const locationsData = locations.locations;

const Slideshow = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === locationsData.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? locationsData.length - 1 : prevIndex - 1));
  };

  return (
    <div className="slideshow__container">
      <div className="slideshow__image">
        <img src={locationsData[currentIndex].cover} alt={locationsData[currentIndex].title} />
      </div>
      <div className="slideshow__info">
        <h2>{locationsData[currentIndex].title}</h2>
        <p>{locationsData[currentIndex].description}</p>
        <p>Hosted by: {locationsData[currentIndex].host.name}</p>
        <p>Location: {locationsData[currentIndex].location}</p>
        <p>Rating: {locationsData[currentIndex].rating}</p>
        <button onClick={handlePrevious}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Slideshow;
