import React from "react";
import locations from "../locations.json";

const AccommodationDetails = ({ accommodationData }) => {
  // Check if accommodationData is defined
  if (!accommodationData) {
    return <p>Loading...</p>; // or any other appropriate fallback
  }

  // Extracting data from the accommodation object
  const {
    title,
    cover,
    pictures,
    description,
    host,
    rating,
    location,
    equipments,
    tags,
  } = accommodationData;

  return (
    <div>
      <h1>{title}</h1>
      <img src={cover} alt={title} />

      <h2>Host: {host.name}</h2>
      <img src={host.picture} alt={host.name} />

      <p>Rating: {rating}</p>
      <p>Location: {location}</p>

      <h3>Description:</h3>
      <p>{description}</p>

      <h3>Equipments:</h3>
      <ul>
        {equipments.map((equipment, index) => (
          <li key={index}>{equipment}</li>
        ))}
      </ul>

      <h3>Tags:</h3>
      <ul>
        {tags.map((tag, index) => (
          <li key={index}>{tag}</li>
        ))}
      </ul>

      <h3>Pictures:</h3>
      <div>
        {pictures.map((picture, index) => (
          <img key={index} src={picture} alt={`${title} - ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default AccommodationDetails;
