import React, { useState } from "react";
import ArrowUp from "../assets/arrowup.png";
import "../About.css";

const AboutList = () => {
    const initialItems = [
        { title: "Fiabilité", description: "This is the description for Fiabilité.", isOpen: false },
        { title: "Respect", description: "This is the description for Respect.", isOpen: false },
        { title: "Service", description: "This is the description for Service.", isOpen: false },
        { title: "Sécurité", description: "This is the description for Sécurité.", isOpen: false }
    ];

    const [items, setItems] = useState(initialItems);

    const toggleDescription = (index) => {
        const updatedItems = items.map((item, i) =>
            i === index ? { ...item, isOpen: !item.isOpen } : item
        );
        setItems(updatedItems);
    };

    return (
        <div id="list__container">
            <ul id="list">
                {items.map((item, index) => (
                    <div key={index} className="list__item--container">
                        <li className="list__item" onClick={() => toggleDescription(index)}>
                            {item.title}
                        </li>
                        <img className={`arrowup ${item.isOpen ? 'rotate' : ''}`} src={ArrowUp} alt="arrowup" onClick={() => toggleDescription(index)} />
                        {item.isOpen && (
                            <div className="description__container">
                                <div className="description">
                                    <p>{item.description}</p>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </ul>
        </div>
    );
}

export default AboutList;
