import React from "react";
import "../Locations.css";
import locations from "../locations.json";

const Informations = ({ id }) => {
    // Find the location with the matching ID
    const locationInfo = locations.find(location => location.id === id);

    if (!locationInfo) {
        return <div>No information found for this ID.</div>;
    }

    return (
        <div className="infos__container">
            <h1 className="infos__title">{locationInfo.title}</h1>
            <p className="infos__description">{locationInfo.description}</p>
            <div className="infos__host">
                <img src={locationInfo.host.picture} alt="Host" className="infos__host--picture" />
                <p className="infos__host--name">{locationInfo.host.name}</p>
            </div>
            <p className="infos__rating">{locationInfo.rating}</p>
            <p className="infos__location">{locationInfo.location}</p>
            <div className="infos__equipments">
                <h2>Equipments</h2>
                <ul>
                    {locationInfo.equipments.map((equipment, index) => (
                        <li key={index}>{equipment}</li>
                    ))}
                </ul>
            </div>        
        </div>
    );
};

export default Informations;
