/* eslint-disable jsx-a11y/img-redundant-alt */
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import locations from "../locations.json";
import ArrowLeft from '../assets/arrow-left.png';
import ArrowRight from '../assets/arrow-right.png';

const Slider = () => {
  const { id } = useParams();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Function to get location by ID
  const getLocationById = (id) => {
    return locations.find(location => location.id === id);
  };

  const location = getLocationById(id);

  // Check if location is found
  if (!location) {
    return <div>No location found for ID {id}</div>;
  }

  const nextSlide = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === location.pictures.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? location.pictures.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slider__container">
      <button onClick={prevSlide} className="arrow-button">
        <img src={ArrowLeft} alt="Previous" />
      </button>
      <div className="slider__pictures">
        {location.pictures.map((picture, index) => (
          <img
            key={index}
            src={picture}
            alt={`Location ${location.id} Picture ${index}`}
            style={{
              display: index === currentImageIndex ? "block" : "none"
            }}
          />
        ))}
      </div>
      <button onClick={nextSlide} className="arrow-button">
        <img src={ArrowRight} alt="Next" />
      </button>
    </div>
  );
};

export default Slider;
