import React, { useState } from "react";
import ArrowUp from "../assets/arrowup.png";
import "../About.css";

const AboutList = () => {
    const [showDescription, setShowDescription] = useState(false);

    const toggleDescription = () => {
        setShowDescription(!showDescription);
    };

    return (
        <div id="list__container">
            <ul id="list">
                <div className="list__item--container">
                    <li className="list__item" onClick={toggleDescription}>
                        Fiabilité
                    </li>
                    <img className="arrowup" src={ArrowUp} alt="arrowup" onClick={toggleDescription}/>
                </div>
                {showDescription && (
                    <div className="description">
                        <p>This is the description for Fiabilité.</p>
                    </div>
                )}

                <div className="list__item--container">
                    <li className="list__item" onClick={toggleDescription}>
                        Respect
                    </li>
                    <img className="arrowup" src={ArrowUp} alt="arrowup" onClick={toggleDescription}/>
                </div>
                {showDescription && (
                    <div className="description">
                        <p>This is the description for Respect.</p>
                    </div>
                )}

                <div className="list__item--container">
                    <li className="list__item" onClick={toggleDescription}>
                        Service
                    </li>
                    <img className="arrowup" src={ArrowUp} alt="arrowup" onClick={toggleDescription}/>
                </div>
                {showDescription && (
                    <div className="description">
                        <p>This is the description for Service.</p>
                    </div>
                )}

                <div className="list__item--container">
                    <li className="list__item" onClick={toggleDescription}>
                        Sécurité
                    </li>
                    <img className="arrowup" src={ArrowUp} alt="arrowup" onClick={toggleDescription}/>
                </div>
                {showDescription && (
                    <div className="description">
                        <p>This is the description for Sécurité.</p>
                    </div>
                )}
            </ul>
        </div>
    );
}

export default AboutList;
