import React, { useState } from 'react';

const Slideshow = () => {
  const images = [
    'url_de_votre_image_1.jpg',
    'url_de_votre_image_2.jpg',
    // ... Ajoutez toutes vos images ici
  ];

  const Slideshow = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
    };

    const prevSlide = () => {
      setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
    };

    return (
      <div className="slideshow-container">
        {images.length > 1 && (
          <>
            <button onClick={prevSlide} className="arrow left-arrow">
              &#8249;
            </button>
            <button onClick={nextSlide} className="arrow right-arrow">
              &#8250;
            </button>
          </>
        )}

        {images.length > 1 && (
          <div className="slide-number">{currentSlide + 1}/{images.length}</div>
        )}

        <img src={images[currentSlide]} alt={`Slide ${currentSlide + 1}`} className="slide" />
      </div>
    );
  };

};

export default Slideshow;
