import React, { useState } from "react";
import locations from "../locations.json";

const Slideshow = () => {
    const locationsData = locations.locations || [];
    const [currentSlide, setCurrentSlide] = useState(1);

    const plusSlides = (increment) => {
        setCurrentSlide((prevSlide) => prevSlide + increment);
    };

    const goToSlide = (index) => {
        setCurrentSlide(index);
    };

    return (
        <div>
            <div className="slideshow-container">
                {locationsData.map((location, index) => (
                    <div className={`mySlides fade ${index + 1 === currentSlide ? "show" : ""}`} key={index}>
                        <img src={location.image} alt={location.name} />
                        <div className="text">{location.name}</div>
                    </div>
                ))}
                <a className="prev" onClick={() => plusSlides(-1)}>&#10094;</a>
                <a className="next" onClick={() => plusSlides(1)}>&#10095;</a>
            </div>
            <br />
            <div style={{ textAlign: "center" }}>
                {locationsData.map((location, index) => (
                    <span
                        className={`dot ${index + 1 === currentSlide ? "active" : ""}`}
                        key={index}
                        onClick={() => goToSlide(index + 1)}
                    ></span>
                ))}
            </div>
        </div>
    );
};

export default Slideshow;
