import React from "react";
import ArrowUp from "../assets/arrowup.png";
import "../Locations.css";


const Button = ({ show, setShow, arrowRotation, setArrowRotation, text, content }) => {
    const toggle = () => {
        setShow(!show);
        setArrowRotation(!arrowRotation);
    };

    return (
        <div className="infos__btn--container">
            <button type="button" className="infos__button" onClick={toggle}>
                {text}
            </button>
            <img className={`arrow-up ${arrowRotation ? 'rotate' : ''}`} src={ArrowUp} alt="arrowup" onClick={toggle} />
            {show && (
                <div>
                    {content}
                </div>
            )}
        </div>
    );
};

export default Button;
